import Typography from '@mui/material/Typography';
import { ThemeProvider, styled, theme, createTheme  } from '@mui/material/styles'
import Card from '@mui/material/Card';
import React, {useState, useRef} from 'react';
import { makeStyles } from '@mui/styles';
import Grid from '@mui/material/Grid'
import { red, green, blue } from '@mui/material/colors';

const CreationDifferentPartGallery = ({ref1,ref2, text1,text2}) => {

    const useStyles = makeStyles({
        picturesGallery: {
            width: "15em",
            height: 'auto',
            borderRadius: "0.5em",
        },
        mrhankey: {
            boxShadow: "inset 2em 2em 0.8em grey, 2em 2em 0.7em grey",
            width: "0.1em",
            height: 'auto',
            borderRadius: "0.5em"
        },
    })

    const Root = styled('div')(({ theme }) => ({
        [theme.breakpoints.down('md')]: {
            backgroundColor: "red",
        },
        [theme.breakpoints.up('md')]: {
            backgroundColor: "green",
        },
    }))

const classes = useStyles();

        return (
            <Root>

                    <Typography variant="h6" >
                        {text1}
                    </Typography>
                    <Typography variant="picturePartGallery" >
                        <img src={text2} ref={ref1} id={ref2} className={classes.picturesGallery}/>
                    </Typography>

            </Root>
        )
    }


export default CreationDifferentPartGallery
