import Card from '@mui/material/Card';
import React, {useState, useRef} from 'react';
import { makeStyles, styled } from '@mui/styles';

const CreationDifferentPartGallery = ({ref1,ref2, text1,text2}) => {

    const useStyles = makeStyles({
        picturesGallery: {
            width: "15em",
            height: 'auto',
            borderRadius: "0.5em",
        },
        mrhankey: {
            boxShadow: "inset 2em 2em 0.8em grey, 2em 2em 0.7em grey",
            width: "0.1em",
            height: 'auto',
            borderRadius: "0.5em"
        },
    })

    const styles = (theme) => ({
        root: {
            [theme.breakpoints.down('md')]: {
                backgroundColor: "red"
            },
            [theme.breakpoints.up('md')]: {
                backgroundColor: "green"
            }
        }
    })

const classes = useStyles();
        return (
                <Card className={style.biloute} sx={{
                    textAlign: 'center',
                    paddingBottom: "7em",
                    marginLeft: "2em",
                    boxShadow: 10
                }}>   
                    <Typography variant="h6" >
                        {text1}
                    </Typography>
                    <Typography variant="picturePartGallery" >
                        <img src={text2} ref={ref1} id={ref2} className={classes.picturesGallery}/>
                    </Typography>
                </Card>

        )
    }


export default CreationDifferentPartGallery
